import React ,{useState} from 'react';
import Calendar from 'react-calendar';
import {fascalendar, faCalendar } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'





const BookingForm = () =>{
    const [booking, setBooking] =useState({
        name: '',
        surname: '',
        cellphone:'',
        appointment:'',
        styling:''
    })

    const [show, setShow] = useState({hide:'off'})
    const {hide} = show;

    const {name, surname, cellphone, appointment, styling } = booking

    const showCalender = () =>{
            setShow({
                hide:'on'
            })
            console.log()
    }

    const onChange = () => setBooking()

    return(
        <div>
        <form>
            <h2 className="text-primary">Add Booking</h2>
            <input
                type="text"
                placeholder="Name"
                name="name"
                value={name}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="Surname"
                name="surname"
                value={surname}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="cellphone"
                name="cellphone"
                value={cellphone}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="Style"
                name="styling"
                value={styling}
                onChange={onChange}
                required
            />


        </form>

            <button onClick={showCalender} >
                Appointment Date
            </button>
        </div>
    )
}

export default BookingForm;
