import React ,{useState, useContext} from 'react';
import BookingContext from '../../context/booking/bookingContext';
import Calendar from 'react-calendar';
import {fascalendar, faCalendar, faCalendarAlt } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'







const BookingForm = () =>{

    const bookingContext = useContext(BookingContext);

    const [booking, setBooking] =useState({
        name: '',
        surname: '',
        cellphone:'',
        appointment:'',
        styling:''
    })

    const [cal, setCall] = useState({ date: new Date,})


    const {name, surname, cellphone, appointment, styling } = booking

    const {date} =cal

    const onChanged = date => setCall({})


    const onChange = (e) => setBooking({...booking, [e.target.name]: e.target.value});

    const onSubmit = (e) =>{
        e.preventDefault();
        bookingContext.addBooking(booking);
        setBooking({
            name: '',
            surname: '',
            cellphone:'',
            appointment:'',
            styling:''
        })
    }

    return(

        <form onSubmit={onSubmit}>
            <h2 className="text-primary">Add Booking</h2>
            <input
                type="text"
                placeholder="Name"
                name="name"
                value={name}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="Surname"
                name="surname"
                value={surname}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="cellphone"
                name="cellphone"
                value={cellphone}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="Style"
                name="styling"
                value={styling}
                onChange={onChange}
                required
            />
             <input
                type="text"
                placeholder="Appointment"
                name="appointment"
                value={date}
                onChange={onChange}
                required
            />





            <p>Choose the date appointment date on the calendar</p>
            <FontAwesomeIcon icon={faCalendar} />
            < Calendar
                onChange={onChanged}
                value={date}
            />

            <div>
                <input type="submit" value="Book An Appointment" className="btn btn-primary btn-block" />
            </div>

            {console.log(date)}
        </form>

    )
}

export default BookingForm;
