import React ,{useState, useContext} from 'react';
import bookingContext from '../../context/booking/bookingContext';
import Calendar from 'react-calendar';
import {fascalendar, faCalendar, faCalendarAlt } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'







const BookingForm = () =>{

    const BookingContext = useContext(bookingContext);

    const [booking, setBooking] =useState({
        name: '',
        surname: '',
        cellphone:'',
        appointment:'',
        styling:''
    })

    const [show, setShow] = useState({hide:'off'})
    const {hide} = show;

    const {name, surname, cellphone, appointment, styling } = booking

    const showCalender = () => setShow({hide:'on'}) 


    const onChange = (e) => setBooking({...booking, [e.target.name]: e.target.value});

    const onSubmit = (e) =>{
        e.preventDefault();
        BookingContext.addBooking(booking);
        setBooking({
            name: '',
            surname: '',
            cellphone:'',
            appointment:'',
            styling:''
        })
    }

    return(

        <form onSubmit={onSubmit}>
            <h2 className="text-primary">Add Booking</h2>
            <input
                type="text"
                placeholder="Name"
                name="name"
                value={name}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="Surname"
                name="surname"
                value={surname}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="cellphone"
                name="cellphone"
                value={cellphone}
                onChange={onChange}
                required
            />

            <input
                type="text"
                placeholder="Style"
                name="styling"
                value={styling}
                onChange={onChange}
                required
            />
            <p>Choose the date appointment date on the calendar</p>
            <FontAwesomeIcon icon={faCalendar} />
            < Calendar
                onChange={onChange}
                name="appontment"
                value={appointment}
            />

            <div>
                <input type="submit" value="Book An Appointment" className="btn btn-primary btn-block" />
            </div>
        </form>

    )
}

export default BookingForm;
