import React, {useContext} from 'react';
import BookingContext from '../../context/booking/bookingContext';
import AuthContext from '../../context/auth/authContext';
import {faPhone, faCalendarAlt, faClock, faEnvelope } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import PropTypes from 'prop-types'
import { Card, Icon, Image, Button , Message} from 'semantic-ui-react'



const BookingItem = ({booking}) => {
    const bookingContext =  useContext(BookingContext) 
    const authContext = useContext(AuthContext);

    const {client} = authContext;
    const {deleteBooking, setCurrent,clearCurrent} = bookingContext;
    const { name, surname, _id, cellphone, styling, appointment,time } = booking 



    const onDelete = () =>{
        deleteBooking(_id);
        clearCurrent();
    }

    return(
        <Card fluid>
        <Image src='' wrapped ui={false} />
        <Card.Content>
          <Card.Header>{booking.name}</Card.Header>
          <Card.Meta>
            <span className='date'>Date Joined: {" "}{booking.date.slice(0,10)}</span>
          </Card.Meta>
          <Card.Description>
          <Message floating  ><Icon name='user' />{booking.name}{" "}{booking.surname}</Message>
            <Message floating  ><Icon name='phone' />{booking.cellphone}</Message>
            <Message floating  ><Icon name='phone' />{booking.cellphone}</Message>
            <Message floating  ><Icon name='calendar' />{booking.appointment}</Message>
            <Message floating  ><Icon name='clock' />{booking.time}</Message>
          </Card.Description>
        </Card.Content>
        <Card.Content extra>
          <Button.Group widths={2}>
              <Button  color="teal" content="Edit" onClick={()=> setCurrent(booking)} />
              <Button  color="pink" content="Delete" onClick={onDelete} />
          </Button.Group>
        </Card.Content>
      </Card>
    )
}

BookingItem.prototype= {
    booking : PropTypes.object.isRequired
}

export default BookingItem
